// toggleList function
toggleList.addEventListener('click', function () {
	if (listDiv.style.display == 'none') {
		toggleList.textContent = 'Hide list';
		listDiv.style.display = 'block';
	} else {
		listDiv.style.display = 'none';
		toggleList.textContent = 'show list';
	}
});

